css:
.card {
  font-family: Times New Roman;
  font-size: 30px;
  text-align: center;
  background: #FBFBEF;
  margin-top: 42px;
  margin-left: 12%;
  margin-right: 12%;
}
.mobile .card {
  font-size: 22px;
}
.nightMode.card {
  background: #060606;
}

@media screen and (max-width: 900px) {
  .card  {
    margin-left: 3%;
    margin-right: 3%;
  }
}

#answerBox {
  margin-top: 1em;
}

:not(#answerSide) > #questionSide #answerBox {
   visibility: hidden;
}

#def {
  color: #233;
  margin-bottom: .3em;
}
.nightMode #def {
  color: #DDD;
}

#syn {
  font-size: .85em;
  color: #DA8547;
  margin-bottom: .2em;
}

#exsen {
  font-size: .85em;
  color: #233;
  margin-bottom: 1.2em;
}
.nightMode #exsen {
  color: #d0d0d0;
}

#pos  {
  font-size: .8em;
  color: #DA8547;
  margin-bottom: .3em;
}
.nightMode #pos {
  color: #cca;
}

#etym {
  font-size: .7em;
  color: #777;
  margin-bottom: .6em;
}

input {
  font-size: 38px !important;
  width: 80% !important;
  background: #F1F9F6 !important;
  border-radius: 6px;
  padding-left: .3em;
}
.nightMode input {
  background: #060606 !important;
  color: #FFF;
  border-style: solid;
  border-color: #777;
  border-width: thin;
}

.typeGood {
  font-size: 38px;
  background: #13C700;
  color: #000;
  border-radius: 12px;
  padding-right: 3px;
  padding-left: 3px;
}
.mobile .typeGood {
  font-size: 30px;
}
.nightMode .typeGood {
  background: green;
}

.typeBad {
  font-size: 38px;
  background: red;
  color: #000;
  border-radius: 12px;
  padding-right: 3px;
  padding-left: 3px;
}
.mobile .typeBad {
  font-size: 30px;
}

.typeMissed {
  font-size: 38px;
  background: orange;
  color: #000;
  border-radius: 12px;
  padding-right: 3px;
  padding-left: 3px;
}
.mobile .typeMissed {
  font-size: 30px;
}
