css:
td {
  vertical-align: top;
  width: 50%;
}

.card {
  font-family: "Liberation Serif";
  font-size: 30px;
  color: #494949;
  background: #FBFBEF;
  margin-top: 1em;
  margin-left: 12%;
  margin-right: 12%;
}
.mobile .card {
  font-size: 22px;
}
.nightMode.card {
  color: #CCC;
  background: #090909;
}

@media screen and (max-width: 900px) {
  .card  {
    margin-left: 4%;
    margin-right: 4%;
  }
}

#answerBox {
  margin-top: 1em;
}

:not(#answerSide) > #questionSide #answerBox {
   visibility: hidden;
}

#def {
  margin-bottom: .5em;
}

#highlight {
  font-size: .85em;
  color: #C88547;
}
.nightMode #highlight {
  color: #cca;
}

#sub {
  font-size: .85em;
  color: #777;
  margin-bottom: 1em;
}
.nightMode #sub {
  color: #999;
}

input {
  font-size: 36px !important;
  background: #F1F9F6 !important;
  border-radius: 6px;
  padding-left: .3em;
}
.nightMode input {
  background: #080808 !important;
  color: #D8D8D8;
  border-style: solid;
  border-color: #777;
  border-width: thin;
}

.typeGood {
  font-size: 36px;
  background: #13C700;
  color: #000;
  border-radius: 12px;
  padding-right: 3px;
  padding-left: 3px;
}
.mobile .typeGood {
  font-size: 30px;
}
.nightMode .typeGood {
  background: green;
}

.typeBad {
  font-size: 36px;
  background: red;
  color: #000;
  border-radius: 12px;
  padding-right: 3px;
  padding-left: 3px;
}
.mobile .typeBad {
  font-size: 30px;
}

.typeMissed {
  font-size: 36px;
  background: orange;
  color: #000;
  border-radius: 12px;
  padding-right: 3px;
  padding-left: 3px;
}
.mobile .typeMissed {
  font-size: 30px;
}
