css:
.card {
  font-family: Times New Roman;
  font-size: 30px;
  color: #455;
  text-align: center;
  background: #FBFBEF;
  margin-top: 85px;
  margin-left: 12%;
  margin-right: 12%;
}
.mobile .card {
  font-size: 22px;
}
.nightMode.card {
  background: #060606;
}

@media screen and (max-width: 900px) {
  .card  {
    margin-left: 3%;
    margin-right: 3%;
  }
}

#answerBox {
  margin-top: 1em;
}

:not(#answerSide) > #questionSide #answerBox {
   visibility: hidden;
}

#pz {
  font-size: 1.25em;
  margin-bottom: 1.2em;
}
.nightMode #pz {
  color: #CCC;
}

#def {
  margin-bottom: 1.5em;
}
.nightMode #def {
  color: #CCC;
}

#exsen {
  font-size: .85em;
  color: #CCC;
  margin-bottom: .6em;
}

#etym {
  font-size: .85em;
  color: #777;
  margin-bottom: .6em;
}

#syn  {
  font-size: .8em;
  color: #DA8547;
  margin-bottom: .2em;
}

#pos {
  font-size: .8em;
  color: #DA8547;
  margin-bottom: .2em;
}
.nightMode #pos {
  color: #cca;
}
