css:
.card {
  font-family: "Liberation Serif";
  font-size: 30px;
  color: #494949;
  background: #FBFBEF;
  margin-top: 1em;
  margin-left: 12%;
  margin-right: 12%;
}
.mobile .card {
  font-size: 22px;
}
.nightMode.card {
  color: #CCC;
  background: #090909;
}

@media screen and (max-width: 900px) {
  .card  {
    margin-left: 4%;
    margin-right: 4%;
  }
}

#answerBox {
  margin-top: 1em;
}

:not(#answerSide) > #questionSide #answerBox {
   visibility: hidden;
}

#main {
  font-size: 36px;
}

#def {
  margin-bottom: .5em;
}

#highlight {
  font-size: .85em;
  color: #C88547;
}
.nightMode #highlight {
  color: #cca;
}

#sub {
  font-size: .85em;
  color: #777;
  margin-bottom: .8em;
}
.nightMode #sub {
  color: #999;
}
